INSERT INTO `wp_posts` VALUES (17,1,'2025-09-30 21:45:40','2025-09-30 21:45:40','<!-- wp:paragraph -->\n<p>Effective Date: January 1, 2025</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Last Updated: January 1, 2025</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>BREACHFIX (\"we,\" \"our,\" or \"us\") values your privacy and is committed to protecting your personal information. This Privacy Policy explains how we collect, use, store, and protect the data you share with us when you use our website, applications, and services (collectively, the \"Services\").</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. OVERVIEW</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>At BREACHFIX, we believe in transparency about how we handle your personal information. This policy covers all aspects of data collection, usage, and protection across our platform.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Our Commitment to You:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Secure: Industry-standard encryption</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Transparent: Clear data practices</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Your Control: You manage your data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. INFORMATION WE COLLECT</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Personal Information:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Name and email address</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Account login details</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Billing address and payment details</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Profile information and preferences</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Content Information:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Media and text you upload or submit</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Comments and interactions on our platform</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Saved favorites and playlists</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Usage Information:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• IP address and browser type</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Device information and access times</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Pages viewed and features used</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Performance and error logs</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cookies &amp; Tracking Data:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Session cookies for authentication</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Analytics cookies for performance insights</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Preference cookies for personalization</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Marketing cookies (with consent)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3. HOW WE USE YOUR INFORMATION</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Service Delivery:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Provide and maintain our Services</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Process transactions and subscriptions</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Handle pay-per-view payments</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Deliver content and features</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Improvement &amp; Personalization:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Enhance and expand BREACHFIX features</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Personalize your experience</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Recommend relevant content</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Optimize performance</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Communication:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Send service updates and notifications</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Provide customer support</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Share new features and promotions</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Send important security alerts</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Security &amp; Compliance:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Protect against fraud and abuse</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Monitor for security risks</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Comply with legal obligations</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Enforce our terms of service</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4. PAYMENT AND CREDIT CARD INFORMATION</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Secure Payment Processing</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>BREACHFIX does not store your full credit card details on our servers. All payments are processed through secure, PCI-DSS compliant third-party providers such as Stripe.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>What We Don\'t Store:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Full credit card numbers</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• CVV codes</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Expiration dates</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Billing addresses</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>What We Do Store:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Transaction IDs</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Payment status</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Subscription details</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Billing history</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5. HOW WE SHARE INFORMATION</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>WE DO NOT SELL YOUR DATA</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>BREACHFIX does not sell, rent, or trade your personal information to third parties. We only share data in the limited circumstances outlined below.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Service Providers:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Trusted partners who help us operate our platform:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Cloud hosting and infrastructure</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Payment processing services</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Analytics and performance monitoring</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Customer support tools</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Email and notification services</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Legal Requirements:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>We may share data when required by law:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• To comply with legal obligations</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• In response to court orders</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• To protect our rights and property</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• To prevent fraud or abuse</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• In case of business transfers</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>6. COOKIES AND TRACKING</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Essential Cookies:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Required for basic website functionality:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Keep you signed in to your account</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Remember your preferences and settings</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Maintain your session security</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Enable core platform features</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Analytics Cookies:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Help us understand how you use our platform:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Analyze traffic and site performance</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Identify popular content and features</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Monitor for errors and issues</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Improve user experience</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Cookie Management:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>You can disable cookies in your browser settings, but this may limit some functionality of our Services. Essential cookies cannot be disabled as they are required for basic operation.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Browser Cookie Settings:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Chrome: Settings → Privacy and security → Cookies</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Firefox: Settings → Privacy &amp; Security → Cookies</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Safari: Preferences → Privacy → Cookies</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Edge: Settings → Cookies and site permissions</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>7. DATA RETENTION</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>We retain your personal information only as long as necessary to provide our Services, comply with legal obligations, resolve disputes, and enforce our agreements.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Active Accounts:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Account data: While account is active</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Content: Until deleted by user</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Usage data: 24 months</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Payment records: 7 years</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Deleted Accounts:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Personal data: 30 days</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Anonymized analytics: 24 months</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Legal compliance data: As required</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Backup data: 90 days</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>8. SECURITY</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Security Measures:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• End-to-end encryption for sensitive data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Secure servers with restricted access</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Regular security audits and monitoring</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Staff training on data protection</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Security Standards:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Industry-standard encryption (AES-256)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• PCI-DSS compliance for payments</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• HTTPS encryption for all communications</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Regular security updates and patches</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>IMPORTANT: While we implement industry-standard safeguards to protect your data, no method of transmission or storage is 100% secure. We cannot guarantee absolute security but are committed to continuous improvement.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>9. CHILDREN\'S PRIVACY</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Age Restrictions</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>BREACHFIX does not knowingly collect personal data from children under 13 years of age (or the age required by local law in your jurisdiction). Our Services are not intended for children under 13.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>What We Do If We Discover Child Data:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Immediately delete the personal information</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Disable the account if applicable</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Notify parents or guardians if contact information is available</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Report to appropriate authorities if required by law</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>═══════════════════════════════════════════════════════════════════════════════</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>10. YOUR RIGHTS</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Data Subject Rights</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Depending on your location, you may have the following rights regarding your personal data:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Access: Request a copy of your personal data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Correction: Update or correct inaccurate data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Deletion: Request deletion of your data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Portability: Export your data in a portable format</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Restriction: Limit how we process your data</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Objection: Object to certain processing activities</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>How to Exercise Your Rights</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>To exercise any of these rights, please contact us using the information provided in the \"Contact Us\" section below. We will respond to your request within 30 days.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Required Information:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Your full name and email address</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Specific right you want to exercise</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>• Description of the data in question</p>\n<!-- 